mapper.select:
select gamer_id, email, user_name, user_password, account_status, registration_date, registered_from_ip, locale from a_gamer_account where account_status != 'REMOVED_COMPLETELY'
and lower(user_name) like lower(#{userNameOrEmail}) or lower(email) like lower(#{userNameOrEmail})
and gamer_id = #{gamerId}

select gamer_id from a_gamer_account where account_status != 'REMOVED_COMPLETELY'
and lower(user_name) like lower(#{userNameOrEmail}) or lower(email) like lower(#{userNameOrEmail})
and gamer_id = #{gamerId}

mapper.update:
update a_gamer_account
set user_password = #{password}
where gamer_id = #{gamerId}

update a_gamer_account
set email = #{email}
where gamer_id = #{gamerId}

update a_gamer_account
set user_name = #{userName}
where gamer_id = #{gamerId}

update a_gamer_account
set account_status = #{status}
where gamer_id = #{gamerId}
